import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Timeline from '../components/Timeline';
import TaskBoard from '../components/TaskBoard';

/**
 * DealRoom - Post-accept workspace for lenders
 * Chunk 5: Deal Room shell with nav structure
 */
function DealRoom() {
  const { dealId } = useParams();
  const navigate = useNavigate();
  const [deal, setDeal] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeModule, setActiveModule] = useState('timeline');
  const role = localStorage.getItem('role');
  const isLender = role === 'Lender';

  useEffect(() => {
    loadDeal();
  }, [dealId]);

  async function loadDeal() {
    setLoading(true);
    setError(null);
    try {
      const res = await api.get(`/api/deals/${dealId}/`);
      
      // Check if response is valid JSON
      if (!res.data || typeof res.data !== 'object') {
        console.error('Invalid deal response:', res);
        setError('Invalid response format from server. Expected JSON but received: ' + typeof res.data);
        return;
      }
      
      // Check if response has expected deal structure
      if (!res.data.deal_id && !res.data.id) {
        console.error('Deal response missing ID:', res.data);
        setError('Deal response is missing required fields. Please check the server response.');
        return;
      }
      
      setDeal(res.data);
    } catch (err) {
      console.error('Failed to load deal:', err);
      console.error('Error response:', err.response);
      console.error('Error data:', err.response?.data);
      
      // Check if response is XML (indicates wrong content-type)
      if (err.response?.data && typeof err.response.data === 'string' && err.response.data.trim().startsWith('<?xml')) {
        setError('Server returned XML instead of JSON. Please check API configuration and ensure Content-Type is application/json.');
        return;
      }
      
      const errorMsg = err.response?.data?.error || 
                       err.response?.data?.detail || 
                       err.message || 
                       'Failed to load deal';
      setError(errorMsg);
      
      // Check if it's a permission error
      if (err.response?.status === 403 || err.response?.status === 404) {
        setError('You do not have permission to view this deal, or the deal does not exist.');
      }
    } finally {
      setLoading(false);
    }
  }

  // Navigation modules
  const modules = [
    { id: 'timeline', label: 'Timeline', icon: 'üìÖ' },
    { id: 'tasks', label: 'Tasks', icon: '‚úÖ' },
    { id: 'documents', label: 'Documents', icon: 'üìÑ' },
    { id: 'underwriter-report', label: "Underwriter's Report", icon: 'üìä' },
    { id: 'consultants', label: 'Consultants', icon: 'üë•' },
    { id: 'legal-workspace', label: 'Legal Workspace', icon: '‚öñÔ∏è' },
    ...(deal?.facility_type === 'development' ? [{ id: 'drawdowns', label: 'Drawdowns', icon: 'üí∞' }] : []),
    { id: 'audit-log', label: 'Audit Log', icon: 'üìã' },
    { id: 'reporting', label: 'Reporting', icon: 'üìà' },
  ];

  if (loading) {
    return (
      <div style={commonStyles.container}>
        <p style={{ textAlign: 'center', color: theme.colors.textSecondary }}>Loading deal...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={commonStyles.container}>
        <div style={{
          ...commonStyles.card,
          background: theme.colors.errorLight,
          color: theme.colors.errorDark,
          padding: theme.spacing.lg,
          marginBottom: theme.spacing.lg,
        }}>
          <h2 style={{ margin: `0 0 ${theme.spacing.md} 0` }}>Error Loading Deal</h2>
          <p style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>{error}</p>
          <Button onClick={() => navigate('/lender/applications')}>Back to Applications</Button>
        </div>
      </div>
    );
  }

  if (!deal) {
    return (
      <div style={commonStyles.container}>
        <p style={{ textAlign: 'center', color: theme.colors.textSecondary }}>Deal not found</p>
        <Button onClick={() => navigate('/lender/applications')}>Back to Applications</Button>
      </div>
    );
  }

  // Get status badge variant
  const getStatusBadge = (status) => {
    const statusMap = {
      active: { variant: 'success', label: 'Active' },
      completed: { variant: 'info', label: 'Completed' },
      cancelled: { variant: 'error', label: 'Cancelled' },
      on_hold: { variant: 'warning', label: 'On Hold' },
    };
    const statusInfo = statusMap[status] || { variant: 'info', label: status };
    return <Badge variant={statusInfo.variant}>{statusInfo.label}</Badge>;
  };

  // Get facility type label
  const getFacilityTypeLabel = (type) => {
    const typeMap = {
      bridge: 'Bridge Loan',
      term: 'Term Loan',
      development: 'Development Finance',
    };
    return typeMap[type] || type;
  };

  return (
    <div style={commonStyles.container}>
      {/* Header */}
      <div style={{ marginBottom: theme.spacing.xl }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.md, marginBottom: theme.spacing.md }}>
          <Button onClick={() => navigate('/lender/applications')} variant="outline">
            ‚Üê Back
          </Button>
          <h1 style={{
            margin: 0,
            fontSize: theme.typography.fontSize['3xl'],
            fontWeight: theme.typography.fontWeight.bold,
          }}>
            {deal.deal_id || `Deal #${deal.id}`}
          </h1>
          {getStatusBadge(deal.status)}
        </div>

        {/* Deal Summary Bar */}
        <div style={{
          ...commonStyles.card,
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: theme.spacing.lg,
          padding: theme.spacing.lg,
        }}>
          <div>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
              Facility Type
            </p>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.lg, fontWeight: theme.typography.fontWeight.semibold }}>
              {getFacilityTypeLabel(deal.facility_type)}
            </p>
          </div>
          <div>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
              Loan Amount
            </p>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.lg, fontWeight: theme.typography.fontWeight.semibold }}>
              ¬£{deal.commercial_terms?.loan_amount?.toLocaleString() || 'N/A'}
            </p>
          </div>
          <div>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
              Completion Readiness
            </p>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.lg, fontWeight: theme.typography.fontWeight.semibold }}>
              {deal.completion_readiness_score || 0}%
            </p>
          </div>
          <div>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
              Current Stage
            </p>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.lg, fontWeight: theme.typography.fontWeight.semibold }}>
              {deal.current_stage_name || deal.current_stage?.name || 'Not Started'}
            </p>
          </div>
        </div>

        {/* Blockers (Placeholder) */}
        {deal.completion_readiness_breakdown && Object.keys(deal.completion_readiness_breakdown).length > 0 && (
          <div style={{
            ...commonStyles.card,
            background: theme.colors.warningLight,
            border: `1px solid ${theme.colors.warning}`,
            padding: theme.spacing.md,
            marginTop: theme.spacing.md,
          }}>
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, fontWeight: theme.typography.fontWeight.semibold }}>
              ‚ö†Ô∏è Blockers: {Object.keys(deal.completion_readiness_breakdown).length} item(s) preventing completion
            </p>
          </div>
        )}
      </div>

      {/* Main Content Area */}
      <div style={{ display: 'grid', gridTemplateColumns: '250px 1fr', gap: theme.spacing.xl }}>
        {/* Left Navigation */}
        <div style={{
          ...commonStyles.card,
          padding: 0,
          height: 'fit-content',
          position: 'sticky',
          top: theme.spacing.lg,
        }}>
          <div style={{
            padding: theme.spacing.md,
            borderBottom: `1px solid ${theme.colors.gray200}`,
            background: theme.colors.gray50,
          }}>
            <h3 style={{ margin: 0, fontSize: theme.typography.fontSize.lg }}>Modules</h3>
          </div>
          <nav style={{ display: 'flex', flexDirection: 'column' }}>
            {modules.map((module) => (
              <button
                key={module.id}
                onClick={() => setActiveModule(module.id)}
                style={{
                  padding: theme.spacing.md,
                  background: activeModule === module.id ? theme.colors.primaryLight : 'transparent',
                  border: 'none',
                  borderLeft: activeModule === module.id ? `4px solid ${theme.colors.primary}` : '4px solid transparent',
                  color: activeModule === module.id ? theme.colors.primary : theme.colors.textPrimary,
                  textAlign: 'left',
                  cursor: 'pointer',
                  fontSize: theme.typography.fontSize.base,
                  fontWeight: activeModule === module.id ? theme.typography.fontWeight.semibold : theme.typography.fontWeight.normal,
                  transition: 'all 0.2s',
                  display: 'flex',
                  alignItems: 'center',
                  gap: theme.spacing.sm,
                }}
                onMouseEnter={(e) => {
                  if (activeModule !== module.id) {
                    e.currentTarget.style.background = theme.colors.gray50;
                  }
                }}
                onMouseLeave={(e) => {
                  if (activeModule !== module.id) {
                    e.currentTarget.style.background = 'transparent';
                  }
                }}
              >
                <span style={{ fontSize: theme.typography.fontSize.lg }}>{module.icon}</span>
                <span>{module.label}</span>
              </button>
            ))}
          </nav>
        </div>

        {/* Module Content */}
        <div>
          {activeModule === 'timeline' && (
            <Timeline dealId={dealId} />
          )}

          {activeModule === 'tasks' && (
            <TaskBoard dealId={dealId} />
          )}

          {activeModule === 'documents' && (
            <div style={commonStyles.card}>
              <h2 style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>Documents</h2>
              <p style={{ color: theme.colors.textSecondary }}>
                Secure document vault with categories and permissions. (Chunk 7)
              </p>
            </div>
          )}

          {activeModule === 'underwriter-report' && (
            <div style={commonStyles.card}>
              <h2 style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>Underwriter's Report</h2>
              <p style={{ color: theme.colors.textSecondary }}>
                Underwriter's report from the original application. (Integration with existing component)
              </p>
              {deal.application && (
                <div style={{ marginTop: theme.spacing.md }}>
                  <Button
                    variant="outline"
                    onClick={() => navigate(`/lender/applications/${deal.application}`)}
                  >
                    View Original Application
                  </Button>
                </div>
              )}
            </div>
          )}

          {activeModule === 'consultants' && (
            <div style={commonStyles.card}>
              <h2 style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>Consultants</h2>
              <p style={{ color: theme.colors.textSecondary }}>
                Consultant appointment and management. (Chunk 8)
              </p>
            </div>
          )}

          {activeModule === 'legal-workspace' && (
            <div style={commonStyles.card}>
              <h2 style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>Legal Workspace</h2>
              <p style={{ color: theme.colors.textSecondary }}>
                CP checklist and requisitions queue. (Chunk 9)
              </p>
            </div>
          )}

          {activeModule === 'drawdowns' && (
            <div style={commonStyles.card}>
              <h2 style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>Drawdowns</h2>
              <p style={{ color: theme.colors.textSecondary }}>
                Development finance drawdown workflow. (Chunk 10)
              </p>
            </div>
          )}

          {activeModule === 'audit-log' && (
            <div style={commonStyles.card}>
              <h2 style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>Audit Log</h2>
              <p style={{ color: theme.colors.textSecondary }}>
                Immutable audit log of all deal actions. (Will be populated as features are implemented)
              </p>
            </div>
          )}

          {activeModule === 'reporting' && (
            <div style={commonStyles.card}>
              <h2 style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>Reporting</h2>
              <p style={{ color: theme.colors.textSecondary }}>
                Deal metrics and dashboards. (Chunk 11)
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default DealRoom;
