import React, { useState, useEffect } from 'react';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Button from './Button';
import Input from './Input';
import Textarea from './Textarea';
import Select from './Select';

function InformationRequestBuilder({ applicationId, isOpen, onClose, onSuccess }) {
  const [title, setTitle] = useState('Information Request');
  const [notes, setNotes] = useState('');
  const [items, setItems] = useState([{ title: '', description: '', due_days: 7, document_type_id: '' }]);
  const [documentTypes, setDocumentTypes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (isOpen) {
      loadDocumentTypes();
    }
  }, [isOpen]);

  async function loadDocumentTypes() {
    try {
      const res = await api.get('/api/documents/types/?loan_type=business_finance');
      setDocumentTypes(res.data || []);
    } catch (err) {
      console.error('Failed to load document types:', err);
    }
  }

  function addItem() {
    setItems([...items, { title: '', description: '', due_days: 7, document_type_id: '' }]);
  }

  function removeItem(index) {
    setItems(items.filter((_, i) => i !== index));
  }

  function updateItem(index, field, value) {
    const newItems = [...items];
    newItems[index] = { ...newItems[index], [field]: value };
    setItems(newItems);
  }

  function calculateDueDate(days) {
    const date = new Date();
    date.setDate(date.getDate() + parseInt(days) || 7);
    return date.toISOString().split('T')[0];
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setError(null);

    // Validate
    if (!title.trim()) {
      setError('Please provide a title for the request');
      return;
    }

    if (items.length === 0) {
      setError('Please add at least one request item');
      return;
    }

    for (let i = 0; i < items.length; i++) {
      const item = items[i];
      if (!item.title.trim()) {
        setError(`Item ${i + 1}: Please provide a title`);
        return;
      }
    }

    setLoading(true);
    try {
      const requestData = {
        title: title.trim(),
        notes: notes.trim(),
        items: items.map(item => ({
          title: item.title.trim(),
          description: item.description.trim(),
          due_date: calculateDueDate(item.due_days),
          document_type_id: item.document_type_id || null,
        })),
      };

      await api.post(`/api/applications/${applicationId}/information-requests/`, requestData);
      
      // Reset form
      setTitle('Information Request');
      setNotes('');
      setItems([{ title: '', description: '', due_days: 7, document_type_id: '' }]);
      
      if (onSuccess) {
        onSuccess();
      }
      onClose();
    } catch (err) {
      console.error('Failed to create information request:', err);
      setError(err.response?.data?.error || 'Failed to create information request');
    } finally {
      setLoading(false);
    }
  }

  if (!isOpen) return null;

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
      padding: theme.spacing.lg,
    }}>
      <div style={{
        ...commonStyles.card,
        maxWidth: '800px',
        width: '100%',
        maxHeight: '90vh',
        overflowY: 'auto',
        position: 'relative',
      }}>
        <div style={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: theme.spacing.lg,
          borderBottom: `1px solid ${theme.colors.gray200}`,
          paddingBottom: theme.spacing.md,
        }}>
          <h2 style={{ margin: 0 }}>Create Information Request</h2>
          <button
            onClick={onClose}
            style={{
              background: 'transparent',
              border: 'none',
              fontSize: '24px',
              cursor: 'pointer',
              color: theme.colors.textSecondary,
              padding: 0,
              width: '32px',
              height: '32px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
          >
            Ã—
          </button>
        </div>

        {error && (
          <div style={{
            padding: theme.spacing.md,
            background: theme.colors.errorLight,
            color: theme.colors.errorDark,
            borderRadius: theme.borderRadius.md,
            marginBottom: theme.spacing.lg,
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: theme.spacing.lg }}>
            <label style={{
              display: 'block',
              marginBottom: theme.spacing.xs,
              fontWeight: theme.typography.fontWeight.semibold,
            }}>
              Request Title
            </label>
            <Input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="e.g., Additional Financial Documents"
              required
            />
          </div>

          <div style={{ marginBottom: theme.spacing.lg }}>
            <label style={{
              display: 'block',
              marginBottom: theme.spacing.xs,
              fontWeight: theme.typography.fontWeight.semibold,
            }}>
              Notes (Optional)
            </label>
            <Textarea
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Add any additional context or instructions..."
              rows={3}
            />
          </div>

          <div style={{ marginBottom: theme.spacing.lg }}>
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: theme.spacing.md,
            }}>
              <label style={{
                fontWeight: theme.typography.fontWeight.semibold,
              }}>
                Request Items
              </label>
              <Button
                type="button"
                variant="outline"
                size="sm"
                onClick={addItem}
              >
                + Add Item
              </Button>
            </div>

            <div style={{ display: 'flex', flexDirection: 'column', gap: theme.spacing.md }}>
              {items.map((item, index) => (
                <div key={index} style={{
                  padding: theme.spacing.md,
                  border: `1px solid ${theme.colors.gray200}`,
                  borderRadius: theme.borderRadius.md,
                  background: theme.colors.gray50,
                }}>
                  <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    marginBottom: theme.spacing.sm,
                  }}>
                    <h4 style={{ margin: 0, fontSize: theme.typography.fontSize.base }}>
                      Item {index + 1}
                    </h4>
                    {items.length > 1 && (
                      <Button
                        type="button"
                        variant="danger"
                        size="sm"
                        onClick={() => removeItem(index)}
                      >
                        Remove
                      </Button>
                    )}
                  </div>

                  <div style={{ marginBottom: theme.spacing.sm }}>
                    <label style={{
                      display: 'block',
                      marginBottom: theme.spacing.xs,
                      fontSize: theme.typography.fontSize.sm,
                      fontWeight: theme.typography.fontWeight.medium,
                    }}>
                      Title *
                    </label>
                    <Input
                      value={item.title}
                      onChange={(e) => updateItem(index, 'title', e.target.value)}
                      placeholder="e.g., Bank Statements (Last 3 months)"
                      required
                    />
                  </div>

                  <div style={{ marginBottom: theme.spacing.sm }}>
                    <label style={{
                      display: 'block',
                      marginBottom: theme.spacing.xs,
                      fontSize: theme.typography.fontSize.sm,
                      fontWeight: theme.typography.fontWeight.medium,
                    }}>
                      Description
                    </label>
                    <Textarea
                      value={item.description}
                      onChange={(e) => updateItem(index, 'description', e.target.value)}
                      placeholder="Provide details about what is needed..."
                      rows={2}
                    />
                  </div>

                  <div style={{
                    display: 'grid',
                    gridTemplateColumns: '1fr 1fr',
                    gap: theme.spacing.md,
                  }}>
                    <div>
                      <label style={{
                        display: 'block',
                        marginBottom: theme.spacing.xs,
                        fontSize: theme.typography.fontSize.sm,
                        fontWeight: theme.typography.fontWeight.medium,
                      }}>
                        Document Type (Optional)
                      </label>
                      <select
                        value={item.document_type_id}
                        onChange={(e) => updateItem(index, 'document_type_id', e.target.value)}
                        style={{
                          width: '100%',
                          padding: theme.spacing.sm,
                          border: `1px solid ${theme.colors.gray300}`,
                          borderRadius: theme.borderRadius.md,
                          fontSize: theme.typography.fontSize.base,
                          fontFamily: theme.typography.fontFamily,
                          backgroundColor: theme.colors.white,
                        }}
                      >
                        <option value="">Select document type...</option>
                        {documentTypes.map(dt => (
                          <option key={dt.id} value={dt.id}>
                            {dt.name} {dt.is_required && '(Required)'}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div>
                      <label style={{
                        display: 'block',
                        marginBottom: theme.spacing.xs,
                        fontSize: theme.typography.fontSize.sm,
                        fontWeight: theme.typography.fontWeight.medium,
                      }}>
                        Due In (Days)
                      </label>
                      <Input
                        type="number"
                        value={item.due_days}
                        onChange={(e) => updateItem(index, 'due_days', e.target.value)}
                        min="1"
                        placeholder="7"
                      />
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div style={{
            display: 'flex',
            gap: theme.spacing.md,
            justifyContent: 'flex-end',
            borderTop: `1px solid ${theme.colors.gray200}`,
            paddingTop: theme.spacing.md,
          }}>
            <Button
              type="button"
              variant="outline"
              onClick={onClose}
              disabled={loading}
            >
              Cancel
            </Button>
            <Button
              type="submit"
              variant="primary"
              loading={loading}
              disabled={loading}
            >
              Create Request
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default InformationRequestBuilder;
