import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Button from './Button';
import Badge from './Badge';

function ProfilePreview({ role, profileRoute }) {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      let endpoint = '';
      if (role === 'Borrower') {
        endpoint = '/api/borrowers/profiles/';
      } else if (role === 'Lender') {
        endpoint = '/api/lenders/profiles/';
      } else if (role === 'Consultant') {
        endpoint = '/api/consultants/profiles/';
      }
      
      if (endpoint) {
        const res = await api.get(endpoint);
        const data = Array.isArray(res.data) ? res.data[0] : res.data;
        setProfile(data);
      }
    } catch (err) {
      console.error('Failed to load profile preview:', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={{
        ...commonStyles.card,
        padding: theme.spacing.lg,
      }}>
        <p style={{ color: theme.colors.textSecondary }}>Loading profile...</p>
      </div>
    );
  }

  if (!profile) {
    return (
      <div style={{
        ...commonStyles.card,
        padding: theme.spacing.lg,
      }}>
        <h3 style={{
          fontSize: theme.typography.fontSize.lg,
          fontWeight: theme.typography.fontWeight.semibold,
          margin: `0 0 ${theme.spacing.md} 0`,
        }}>
          Profile
        </h3>
        <p style={{ color: theme.colors.textSecondary, marginBottom: theme.spacing.md }}>
          Complete your profile to get started
        </p>
        <Link to={profileRoute || '/borrower/profile'} style={{ textDecoration: 'none' }}>
          <Button variant="primary" style={{ width: '100%' }}>
            Set Up Profile
          </Button>
        </Link>
      </div>
    );
  }

  // Extract profile information based on role
  let name = '';
  let companyName = '';
  let location = '';
  let companyData = null;
  let verified = false;

  if (role === 'Borrower') {
    name = `${profile.first_name || ''} ${profile.last_name || ''}`.trim() || 'Your Name';
    companyName = profile.company_name || (profile.company_data?.company_name) || '';
    location = [profile.city, profile.county, profile.country].filter(Boolean).join(', ') || 'Location not set';
    companyData = profile.company_data;
    verified = !!profile.company_verified_at;
  } else if (role === 'Lender') {
    name = profile.organisation_name || 'Lender';
    companyName = profile.organisation_name || '';
    location = [profile.city, profile.county, profile.country].filter(Boolean).join(', ') || 'Location not set';
    companyData = profile.company_data;
  } else if (role === 'Consultant') {
    name = profile.organisation_name || 'Consultant';
    companyName = profile.organisation_name || '';
    location = [profile.city, profile.county, profile.country].filter(Boolean).join(', ') || 'Location not set';
    companyData = profile.company_data;
  }

  return (
    <div style={{
      ...commonStyles.card,
      padding: theme.spacing.lg,
    }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: theme.spacing.md }}>
        <h3 style={{
          fontSize: theme.typography.fontSize.lg,
          fontWeight: theme.typography.fontWeight.semibold,
          margin: 0,
        }}>
          Profile
        </h3>
        <Link to={profileRoute || '/borrower/profile'} style={{ textDecoration: 'none' }}>
          <Button variant="outline" style={{ padding: `${theme.spacing.xs} ${theme.spacing.sm}` }}>
            View
          </Button>
        </Link>
      </div>

      {/* Profile Avatar */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: theme.spacing.md,
        marginBottom: theme.spacing.md,
      }}>
        <div style={{
          width: '64px',
          height: '64px',
          borderRadius: theme.borderRadius.full,
          background: `linear-gradient(135deg, ${theme.colors.primary} 0%, ${theme.colors.secondary || theme.colors.primary} 100%)`,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          fontSize: '24px',
          color: theme.colors.white,
          fontWeight: theme.typography.fontWeight.bold,
          flexShrink: 0,
        }}>
          {name.charAt(0).toUpperCase()}
        </div>
        <div style={{ flex: 1, minWidth: 0 }}>
          <p style={{
            margin: 0,
            fontSize: theme.typography.fontSize.base,
            fontWeight: theme.typography.fontWeight.semibold,
            color: theme.colors.textPrimary,
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            whiteSpace: 'nowrap',
          }}>
            {name}
          </p>
          {companyName && (
            <p style={{
              margin: `${theme.spacing.xs} 0 0 0`,
              fontSize: theme.typography.fontSize.sm,
              color: theme.colors.textSecondary,
              overflow: 'hidden',
              textOverflow: 'ellipsis',
              whiteSpace: 'nowrap',
            }}>
              {companyName}
            </p>
          )}
          {location && (
            <p style={{
              margin: `${theme.spacing.xs} 0 0 0`,
              fontSize: theme.typography.fontSize.xs,
              color: theme.colors.textSecondary,
              overflow: 'hidden',
              textOverflow: 'ellipsis',
              whiteSpace: 'nowrap',
            }}>
              üìç {location}
            </p>
          )}
        </div>
      </div>

      {/* Company Verification Badge */}
      {companyData && Object.keys(companyData).length > 0 && (
        <div style={{ marginBottom: theme.spacing.md }}>
          {verified && (
            <Badge variant="success" style={{ marginRight: theme.spacing.xs }}>
              ‚úì Verified
            </Badge>
          )}
          {companyData.company_status && (
            <Badge variant={companyData.company_status === 'active' ? 'success' : 'warning'}>
              {companyData.company_status.charAt(0).toUpperCase() + companyData.company_status.slice(1)}
            </Badge>
          )}
        </div>
      )}

      {/* Quick Stats */}
      {role === 'Borrower' && (
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(2, 1fr)',
          gap: theme.spacing.sm,
          marginTop: theme.spacing.md,
          paddingTop: theme.spacing.md,
          borderTop: `1px solid ${theme.colors.gray200}`,
        }}>
          {profile.directors_data && profile.directors_data.length > 0 && (
            <div>
              <p style={{ margin: 0, fontSize: theme.typography.fontSize.xs, color: theme.colors.textSecondary }}>
                Directors
              </p>
              <p style={{ margin: 0, fontSize: theme.typography.fontSize.base, fontWeight: theme.typography.fontWeight.semibold }}>
                {profile.directors_data.length}
              </p>
            </div>
          )}
          {profile.company_data?.company_number && (
            <div>
              <p style={{ margin: 0, fontSize: theme.typography.fontSize.xs, color: theme.colors.textSecondary }}>
                Company No.
              </p>
              <p style={{ margin: 0, fontSize: theme.typography.fontSize.base, fontWeight: theme.typography.fontWeight.semibold }}>
                {profile.company_data.company_number}
              </p>
            </div>
          )}
        </div>
      )}

      {role === 'Lender' && companyData && (
        <div style={{
          marginTop: theme.spacing.md,
          paddingTop: theme.spacing.md,
          borderTop: `1px solid ${theme.colors.gray200}`,
        }}>
          {companyData.company_type && (
            <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
              Type: <strong>{companyData.company_type.replace(/_/g, ' ')}</strong>
            </p>
          )}
        </div>
      )}

      <Link to={profileRoute || '/borrower/profile'} style={{ textDecoration: 'none', marginTop: theme.spacing.md, display: 'block' }}>
        <Button variant="outline" style={{ width: '100%' }}>
          View Full Profile ‚Üí
        </Button>
      </Link>
    </div>
  );
}

export default ProfilePreview;
