import React, { useState, useEffect } from 'react';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Button from './Button';
import Badge from './Badge';

function CompanyDetails({ companyNumber, apiBasePath = '/api/verification/company' }) {
  const [companyData, setCompanyData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedDocuments, setSelectedDocuments] = useState([]);
  const [saving, setSaving] = useState(false);
  const [downloading, setDownloading] = useState(null);

  useEffect(() => {
    if (companyNumber) {
      loadCompanyDetails();
    }
  }, [companyNumber]);

  const loadCompanyDetails = async () => {
    if (!companyNumber) return;
    
    setLoading(true);
    setError(null);
    try {
      const response = await api.get(`${apiBasePath}/get_full_company_details/`, {
        params: { company_number: companyNumber }
      });
      setCompanyData(response.data);
    } catch (err) {
      console.error('Failed to load company details:', err);
      setError(err.response?.data?.error || 'Failed to load company details');
    } finally {
      setLoading(false);
    }
  };

  const handleDocumentToggle = (doc, category) => {
    const docKey = `${category}_${doc.transaction_id}`;
    setSelectedDocuments(prev => {
      if (prev.find(d => d.transaction_id === doc.transaction_id && d.type === category)) {
        return prev.filter(d => !(d.transaction_id === doc.transaction_id && d.type === category));
      } else {
        return [...prev, { transaction_id: doc.transaction_id, type: category, ...doc }];
      }
    });
  };

  const handleSaveDocuments = async () => {
    if (selectedDocuments.length === 0) {
      setError('Please select at least one document');
      return;
    }

    setSaving(true);
    setError(null);
    try {
      await api.post(`${apiBasePath}/save_selected_documents/`, {
        company_number: companyNumber,
        selected_documents: selectedDocuments,
      });
      alert('Documents saved successfully!');
    } catch (err) {
      console.error('Failed to save documents:', err);
      setError(err.response?.data?.error || 'Failed to save documents');
    } finally {
      setSaving(false);
    }
  };

  const handleDownloadDocument = async (doc, category) => {
    setDownloading(`${category}_${doc.transaction_id}`);
    setError(null);
    try {
      const response = await api.post(`${apiBasePath}/download_company_document/`, {
        company_number: companyNumber,
        transaction_id: doc.transaction_id,
      });

      if (response.data.document_data) {
        // Convert base64 to blob and download
        const byteCharacters = atob(response.data.document_data);
        const byteNumbers = new Array(byteCharacters.length);
        for (let i = 0; i < byteCharacters.length; i++) {
          byteNumbers[i] = byteCharacters.charCodeAt(i);
        }
        const byteArray = new Uint8Array(byteNumbers);
        const blob = new Blob([byteArray], { type: response.data.content_type || 'application/pdf' });
        
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = response.data.filename || `${doc.description || 'document'}.pdf`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);
        window.URL.revokeObjectURL(url);
      } else {
        setError('Document data not available');
      }
    } catch (err) {
      console.error('Failed to download document:', err);
      setError(err.response?.data?.error || 'Failed to download document');
    } finally {
      setDownloading(null);
    }
  };

  const renderDocumentGroup = (title, documents, category) => {
    if (!documents || documents.length === 0) return null;

    return (
      <div style={{ marginBottom: theme.spacing.xl }}>
        <h3 style={{
          fontSize: theme.typography.fontSize.lg,
          fontWeight: theme.typography.fontWeight.semibold,
          marginBottom: theme.spacing.md,
          color: theme.colors.textPrimary,
        }}>
          {title}
        </h3>
        <div style={{
          display: 'grid',
          gap: theme.spacing.sm,
        }}>
          {documents.map((doc) => {
            const docKey = `${category}_${doc.transaction_id}`;
            const isSelected = selectedDocuments.find(d => d.transaction_id === doc.transaction_id && d.type === category);
            const isDownloading = downloading === docKey;

            return (
              <div
                key={doc.transaction_id}
                style={{
                  ...commonStyles.card,
                  padding: theme.spacing.md,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                }}
              >
                <div style={{ flex: 1 }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.sm }}>
                    <input
                      type="checkbox"
                      checked={!!isSelected}
                      onChange={() => handleDocumentToggle(doc, category)}
                      style={{
                        width: '18px',
                        height: '18px',
                        cursor: 'pointer',
                        accentColor: theme.colors.primary,
                      }}
                    />
                    <div>
                      <div style={{ fontWeight: theme.typography.fontWeight.medium }}>
                        {doc.description || 'Document'}
                      </div>
                      <div style={{
                        fontSize: theme.typography.fontSize.sm,
                        color: theme.colors.textSecondary,
                        marginTop: theme.spacing.xs,
                      }}>
                        {doc.date ? new Date(doc.date).toLocaleDateString() : 'Date not available'}
                      </div>
                    </div>
                  </div>
                </div>
                <Button
                  size="sm"
                  variant="outline"
                  onClick={() => handleDownloadDocument(doc, category)}
                  disabled={isDownloading}
                  loading={isDownloading}
                >
                  {isDownloading ? 'Downloading...' : 'Download'}
                </Button>
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  if (!companyNumber) {
    return (
      <div style={commonStyles.card}>
        <p style={{ color: theme.colors.textSecondary }}>Please enter a company number to view details.</p>
      </div>
    );
  }

  if (loading) {
    return (
      <div style={commonStyles.card}>
        <p style={{ color: theme.colors.textSecondary }}>Loading company details...</p>
      </div>
    );
  }

  if (error && !companyData) {
    return (
      <div style={commonStyles.card}>
        <p style={{ color: theme.colors.error }}>{error}</p>
        <Button onClick={loadCompanyDetails} variant="outline" style={{ marginTop: theme.spacing.md }}>
          Retry
        </Button>
      </div>
    );
  }

  if (!companyData) {
    return null;
  }

  const address = companyData.registered_address || {};
  const formattedAddress = [
    address.address_line_1,
    address.address_line_2,
    address.locality,
    address.postal_code,
    address.country,
  ].filter(Boolean).join(', ');

  return (
    <div>
      {error && (
        <div style={{
          background: theme.colors.errorLight,
          color: theme.colors.errorDark,
          padding: theme.spacing.md,
          borderRadius: theme.borderRadius.md,
          marginBottom: theme.spacing.lg,
          border: `1px solid ${theme.colors.error}`,
        }}>
          {error}
        </div>
      )}

      {/* Company Info */}
      <div style={commonStyles.card}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: theme.spacing.md }}>
          <h2 style={{
            fontSize: theme.typography.fontSize['2xl'],
            fontWeight: theme.typography.fontWeight.bold,
            margin: 0,
            color: theme.colors.textPrimary,
          }}>
            {companyData.company_name}
          </h2>
          <Badge variant="success">Verified via Companies House</Badge>
        </div>

        <div style={{ display: 'grid', gap: theme.spacing.sm, marginBottom: theme.spacing.lg }}>
          <div>
            <strong>Company Number:</strong> {companyData.company_number}
          </div>
          <div>
            <strong>Company Type:</strong> {companyData.company_type || 'N/A'}
          </div>
          <div>
            <strong>Status:</strong> {companyData.company_status || 'N/A'}
          </div>
          {companyData.incorporation_date && (
            <div>
              <strong>Incorporation Date:</strong> {new Date(companyData.incorporation_date).toLocaleDateString()}
            </div>
          )}
          {formattedAddress && (
            <div>
              <strong>Registered Address:</strong> {formattedAddress}
            </div>
          )}
        </div>
      </div>

      {/* Active Directors */}
      {companyData.directors && companyData.directors.length > 0 && (
        <div style={commonStyles.card}>
          <h3 style={{
            fontSize: theme.typography.fontSize.lg,
            fontWeight: theme.typography.fontWeight.semibold,
            marginBottom: theme.spacing.md,
            color: theme.colors.textPrimary,
          }}>
            Active Directors
          </h3>
          <div style={{ display: 'grid', gap: theme.spacing.sm }}>
            {companyData.directors.map((director, index) => (
              <div key={index} style={{
                padding: theme.spacing.sm,
                background: theme.colors.gray50,
                borderRadius: theme.borderRadius.md,
              }}>
                <div style={{ fontWeight: theme.typography.fontWeight.medium }}>
                  {director.name || 'N/A'}
                </div>
                {director.nationality && (
                  <div style={{
                    fontSize: theme.typography.fontSize.sm,
                    color: theme.colors.textSecondary,
                    marginTop: theme.spacing.xs,
                  }}>
                    Nationality: {director.nationality}
                  </div>
                )}
                {director.occupation && (
                  <div style={{
                    fontSize: theme.typography.fontSize.sm,
                    color: theme.colors.textSecondary,
                  }}>
                    Occupation: {director.occupation}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Shareholders/PSCs */}
      {companyData.pscs && companyData.pscs.length > 0 && (
        <div style={commonStyles.card}>
          <h3 style={{
            fontSize: theme.typography.fontSize.lg,
            fontWeight: theme.typography.fontWeight.semibold,
            marginBottom: theme.spacing.md,
            color: theme.colors.textPrimary,
          }}>
            Persons with Significant Control
          </h3>
          <div style={{ display: 'grid', gap: theme.spacing.sm }}>
            {companyData.pscs.map((psc, index) => (
              <div key={index} style={{
                padding: theme.spacing.sm,
                background: theme.colors.gray50,
                borderRadius: theme.borderRadius.md,
              }}>
                <div style={{ fontWeight: theme.typography.fontWeight.medium }}>
                  {psc.name || 'N/A'}
                </div>
                {psc.kind && (
                  <div style={{
                    fontSize: theme.typography.fontSize.sm,
                    color: theme.colors.textSecondary,
                    marginTop: theme.spacing.xs,
                  }}>
                    Type: {psc.kind}
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Documents */}
      {companyData.documents && (
        <div style={commonStyles.card}>
          <h3 style={{
            fontSize: theme.typography.fontSize.lg,
            fontWeight: theme.typography.fontWeight.semibold,
            marginBottom: theme.spacing.md,
            color: theme.colors.textPrimary,
          }}>
            Companies House Documents
          </h3>

          {renderDocumentGroup('Incorporation Certificate', companyData.documents.incorporation_cert, 'incorporation_cert')}
          {renderDocumentGroup('Accounts (Last 3 Years)', companyData.documents.accounts, 'accounts')}
          {renderDocumentGroup('Charges', companyData.documents.charges, 'charges')}
          {renderDocumentGroup('Confirmation Statements', companyData.documents.confirmation_statements, 'confirmation_statements')}

          {selectedDocuments.length > 0 && (
            <div style={{ marginTop: theme.spacing.xl, paddingTop: theme.spacing.lg, borderTop: `1px solid ${theme.colors.gray200}` }}>
              <div style={{ marginBottom: theme.spacing.md }}>
                <strong>Selected Documents: {selectedDocuments.length}</strong>
              </div>
              <Button
                onClick={handleSaveDocuments}
                disabled={saving}
                loading={saving}
                variant="primary"
              >
                {saving ? 'Saving...' : 'Save Selected Documents'}
              </Button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default CompanyDetails;
