import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Button from '../components/Button';
import Badge from '../components/Badge';
import Input from '../components/Input';
import Select from '../components/Select';
import { Search, Building2, DollarSign, TrendingUp } from 'lucide-react';

function DealsList() {
  const navigate = useNavigate();
  const [deals, setDeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [facilityTypeFilter, setFacilityTypeFilter] = useState('all');

  useEffect(() => {
    loadDeals();
  }, []);

  async function loadDeals() {
    setLoading(true);
    setError(null);
    try {
      const res = await api.get('/api/deals/');
      
      // Check if response is XML (indicates wrong content-type)
      if (res.data && typeof res.data === 'string' && res.data.trim().startsWith('<?xml')) {
        console.error('Server returned XML instead of JSON:', res.data.substring(0, 200));
        setError('Server returned XML instead of JSON. Please check API configuration.');
        setDeals([]);
        return;
      }
      
      // Ensure we always have an array
      const dealsData = res.data;
      if (Array.isArray(dealsData)) {
        setDeals(dealsData);
      } else if (dealsData && typeof dealsData === 'object') {
        // If it's an object, try to extract an array from common keys
        setDeals(dealsData.results || dealsData.data || []);
      } else {
        console.warn('Unexpected deals response format:', typeof dealsData, dealsData);
        setDeals([]);
      }
    } catch (err) {
      console.error('Failed to load deals:', err);
      console.error('Error response:', err.response);
      console.error('Error data:', err.response?.data);
      
      // Check if response is XML
      if (err.response?.data && typeof err.response.data === 'string' && err.response.data.trim().startsWith('<?xml')) {
        setError('Server returned XML instead of JSON. Please check API configuration and ensure Content-Type is application/json.');
      } else {
        setError(err.response?.data?.error || err.response?.data?.detail || err.message || 'Failed to load deals');
      }
      setDeals([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  }

  // Filter and search deals
  const filteredDeals = deals.filter(deal => {
    const matchesSearch = !searchTerm || 
      deal.deal_id?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      deal.borrower_company?.company_name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      deal.application?.project_details?.address?.toLowerCase().includes(searchTerm.toLowerCase());
    
    const matchesStatus = statusFilter === 'all' || deal.status === statusFilter;
    const matchesFacilityType = facilityTypeFilter === 'all' || deal.facility_type === facilityTypeFilter;
    
    return matchesSearch && matchesStatus && matchesFacilityType;
  });

  const getStatusBadge = (status) => {
    const statusMap = {
      active: { variant: 'success', label: 'Active' },
      completed: { variant: 'info', label: 'Completed' },
      cancelled: { variant: 'error', label: 'Cancelled' },
      on_hold: { variant: 'warning', label: 'On Hold' },
    };
    const statusInfo = statusMap[status] || { variant: 'info', label: status };
    return <Badge variant={statusInfo.variant}>{statusInfo.label}</Badge>;
  };

  const getFacilityTypeLabel = (type) => {
    const typeMap = {
      bridge: 'Bridge Loan',
      term: 'Term Loan',
      development: 'Development Finance',
    };
    return typeMap[type] || type;
  };

  if (loading) {
    return (
      <div style={commonStyles.container}>
        <p style={{ textAlign: 'center', color: theme.colors.textSecondary }}>Loading deals...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={commonStyles.container}>
        <div style={{
          ...commonStyles.card,
          background: theme.colors.errorLight,
          color: theme.colors.errorDark,
          padding: theme.spacing.lg,
        }}>
          <h2 style={{ margin: `0 0 ${theme.spacing.md} 0` }}>Error Loading Deals</h2>
          <p style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>{error}</p>
          <Button onClick={loadDeals}>Retry</Button>
        </div>
      </div>
    );
  }

  return (
    <div style={commonStyles.container}>
      {/* Header */}
      <div style={{ marginBottom: theme.spacing.xl }}>
        <h1 style={{
          fontSize: theme.typography.fontSize['4xl'],
          fontWeight: theme.typography.fontWeight.bold,
          color: theme.colors.textPrimary,
          margin: `0 0 ${theme.spacing.sm} 0`,
        }}>
          Deal Room
        </h1>
        <p style={{
          color: theme.colors.textSecondary,
          fontSize: theme.typography.fontSize.base,
          margin: 0,
        }}>
          Manage and track all active deals
        </p>
      </div>

      {/* Summary Stats */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
        gap: theme.spacing.lg,
        marginBottom: theme.spacing.xl,
      }}>
        <div style={{ ...commonStyles.card, padding: theme.spacing.lg }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.md, marginBottom: theme.spacing.sm }}>
            <Building2 size={24} color={theme.colors.primary} />
            <h3 style={{ margin: 0, fontSize: theme.typography.fontSize.lg }}>Total Deals</h3>
          </div>
          <p style={{ margin: 0, fontSize: theme.typography.fontSize['3xl'], fontWeight: theme.typography.fontWeight.bold, color: theme.colors.primary }}>
            {deals.length}
          </p>
        </div>
        <div style={{ ...commonStyles.card, padding: theme.spacing.lg }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.md, marginBottom: theme.spacing.sm }}>
            <TrendingUp size={24} color={theme.colors.success} />
            <h3 style={{ margin: 0, fontSize: theme.typography.fontSize.lg }}>Active Deals</h3>
          </div>
          <p style={{ margin: 0, fontSize: theme.typography.fontSize['3xl'], fontWeight: theme.typography.fontWeight.bold, color: theme.colors.success }}>
            {deals.filter(d => d.status === 'active').length}
          </p>
        </div>
        <div style={{ ...commonStyles.card, padding: theme.spacing.lg }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.md, marginBottom: theme.spacing.sm }}>
            <DollarSign size={24} color={theme.colors.warning} />
            <h3 style={{ margin: 0, fontSize: theme.typography.fontSize.lg }}>Total Value</h3>
          </div>
          <p style={{ margin: 0, fontSize: theme.typography.fontSize['3xl'], fontWeight: theme.typography.fontWeight.bold, color: theme.colors.warning }}>
            ¬£{deals.reduce((sum, deal) => sum + (parseFloat(deal.commercial_terms?.loan_amount || 0)), 0).toLocaleString('en-GB', { maximumFractionDigits: 0 })}
          </p>
        </div>
      </div>

      {/* Search and Filters */}
      <div style={{ ...commonStyles.card, padding: theme.spacing.lg, marginBottom: theme.spacing.lg }}>
        <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr 1fr', gap: theme.spacing.md, alignItems: 'end' }}>
          <div>
            <label style={commonStyles.formLabel}>Search Deals</label>
            <Input
              type="text"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              placeholder="Search by deal ID, borrower, or address..."
              icon={<Search size={16} color={theme.colors.textSecondary} />}
            />
          </div>
          <div>
            <label style={commonStyles.formLabel}>Status</label>
            <Select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              <option value="all">All Statuses</option>
              <option value="active">Active</option>
              <option value="completed">Completed</option>
              <option value="on_hold">On Hold</option>
              <option value="cancelled">Cancelled</option>
            </Select>
          </div>
          <div>
            <label style={commonStyles.formLabel}>Facility Type</label>
            <Select
              value={facilityTypeFilter}
              onChange={(e) => setFacilityTypeFilter(e.target.value)}
            >
              <option value="all">All Types</option>
              <option value="bridge">Bridge Loan</option>
              <option value="term">Term Loan</option>
              <option value="development">Development Finance</option>
            </Select>
          </div>
        </div>
      </div>

      {/* Deals List */}
      {filteredDeals.length === 0 ? (
        <div style={{ ...commonStyles.card, padding: theme.spacing['3xl'], textAlign: 'center' }}>
          <p style={{ color: theme.colors.textSecondary, fontSize: theme.typography.fontSize.lg }}>
            {deals.length === 0 ? 'No deals found. Accept an application to create a deal.' : 'No deals match your search criteria.'}
          </p>
        </div>
      ) : (
        <div style={{ display: 'grid', gap: theme.spacing.md }}>
          {filteredDeals.map(deal => (
            <div
              key={deal.id}
              onClick={() => navigate(`/lender/deals/${deal.deal_id}`)}
              style={{
                ...commonStyles.card,
                padding: theme.spacing.lg,
                cursor: 'pointer',
                transition: 'all 0.2s',
                border: `2px solid ${deal.status === 'active' ? theme.colors.primary : theme.colors.gray200}`,
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.transform = 'translateY(-2px)';
                e.currentTarget.style.boxShadow = theme.shadows.lg;
                e.currentTarget.style.borderColor = theme.colors.primary;
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.transform = 'translateY(0)';
                e.currentTarget.style.boxShadow = theme.shadows.sm;
                e.currentTarget.style.borderColor = deal.status === 'active' ? theme.colors.primary : theme.colors.gray200;
              }}
            >
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start', marginBottom: theme.spacing.md }}>
                <div style={{ flex: 1 }}>
                  <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.md, marginBottom: theme.spacing.sm }}>
                    <h2 style={{
                      margin: 0,
                      fontSize: theme.typography.fontSize['2xl'],
                      fontWeight: theme.typography.fontWeight.bold,
                      color: theme.colors.textPrimary,
                    }}>
                      {deal.deal_id || `Deal #${deal.id}`}
                    </h2>
                    {getStatusBadge(deal.status)}
                    <Badge variant="outline">{getFacilityTypeLabel(deal.facility_type)}</Badge>
                  </div>
                  <p style={{
                    margin: `0 0 ${theme.spacing.xs} 0`,
                    fontSize: theme.typography.fontSize.base,
                    color: theme.colors.textSecondary,
                  }}>
                    {deal.borrower_company?.company_name || deal.application?.project_details?.address || 'Unknown Borrower'}
                  </p>
                  {deal.application?.project_details?.address && (
                    <p style={{
                      margin: 0,
                      fontSize: theme.typography.fontSize.sm,
                      color: theme.colors.textSecondary,
                    }}>
                      üìç {deal.application.project_details.address}
                    </p>
                  )}
                </div>
                <div style={{ textAlign: 'right', minWidth: '150px' }}>
                  <p style={{
                    margin: `0 0 ${theme.spacing.xs} 0`,
                    fontSize: theme.typography.fontSize.sm,
                    color: theme.colors.textSecondary,
                  }}>
                    Loan Amount
                  </p>
                  <p style={{
                    margin: 0,
                    fontSize: theme.typography.fontSize['2xl'],
                    fontWeight: theme.typography.fontWeight.bold,
                    color: theme.colors.primary,
                  }}>
                    ¬£{parseFloat(deal.commercial_terms?.loan_amount || 0).toLocaleString('en-GB')}
                  </p>
                </div>
              </div>

              <div style={{
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
                gap: theme.spacing.md,
                paddingTop: theme.spacing.md,
                borderTop: `1px solid ${theme.colors.gray200}`,
              }}>
                {deal.current_stage && (
                  <div>
                    <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
                      Current Stage
                    </p>
                    <p style={{ margin: 0, fontSize: theme.typography.fontSize.base, fontWeight: theme.typography.fontWeight.medium }}>
                      {deal.current_stage.name || `Stage ${deal.current_stage.stage_number}`}
                    </p>
                  </div>
                )}
                {deal.accepted_at && (
                  <div>
                    <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
                      Accepted
                    </p>
                    <p style={{ margin: 0, fontSize: theme.typography.fontSize.base, fontWeight: theme.typography.fontWeight.medium }}>
                      {new Date(deal.accepted_at).toLocaleDateString('en-GB', { 
                        year: 'numeric', 
                        month: 'short', 
                        day: 'numeric' 
                      })}
                    </p>
                  </div>
                )}
                {deal.commercial_terms?.interest_rate && (
                  <div>
                    <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
                      Interest Rate
                    </p>
                    <p style={{ margin: 0, fontSize: theme.typography.fontSize.base, fontWeight: theme.typography.fontWeight.medium }}>
                      {deal.commercial_terms.interest_rate}%
                    </p>
                  </div>
                )}
                {deal.commercial_terms?.term_months && (
                  <div>
                    <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
                      Term
                    </p>
                    <p style={{ margin: 0, fontSize: theme.typography.fontSize.base, fontWeight: theme.typography.fontWeight.medium }}>
                      {deal.commercial_terms.term_months} months
                    </p>
                  </div>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default DealsList;
