import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Button from '../components/Button';
import Badge from '../components/Badge';

/**
 * DealRoom - Post-accept workspace for lenders
 * This is a placeholder for Chunk 5 - will be fully implemented later
 */
function DealRoom() {
  const { dealId } = useParams();
  const navigate = useNavigate();
  const [deal, setDeal] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDeal();
  }, [dealId]);

  async function loadDeal() {
    setLoading(true);
    setError(null);
    try {
      // TODO: Replace with actual deal endpoint when backend is ready
      // const res = await api.get(`/api/deals/${dealId}/`);
      // setDeal(res.data);
      
      // Placeholder for now
      setDeal({
        id: dealId,
        name: `Deal #${dealId}`,
        status: 'active',
        application_id: null,
      });
    } catch (err) {
      console.error('Failed to load deal:', err);
      setError('Failed to load deal');
    } finally {
      setLoading(false);
    }
  }

  if (loading) {
    return (
      <div style={commonStyles.container}>
        <p style={{ textAlign: 'center', color: theme.colors.textSecondary }}>Loading deal...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div style={commonStyles.container}>
        <div style={{
          background: theme.colors.errorLight,
          color: theme.colors.errorDark,
          padding: theme.spacing.md,
          borderRadius: theme.borderRadius.md,
          marginBottom: theme.spacing.lg,
        }}>
          {error}
        </div>
        <Button onClick={() => navigate('/lender/applications')}>Back to Applications</Button>
      </div>
    );
  }

  return (
    <div style={commonStyles.container}>
      <div style={{ marginBottom: theme.spacing.xl, display: 'flex', alignItems: 'center', gap: theme.spacing.md }}>
        <Button onClick={() => navigate('/lender/applications')} variant="outline">
          ‚Üê Back
        </Button>
        <h1 style={{
          margin: 0,
          fontSize: theme.typography.fontSize['3xl'],
          fontWeight: theme.typography.fontWeight.bold,
        }}>
          Deal Room: {deal?.name || `Deal #${dealId}`}
        </h1>
        {deal?.status && (
          <Badge variant={deal.status === 'active' ? 'success' : 'info'}>
            {deal.status}
          </Badge>
        )}
      </div>

      <div style={commonStyles.card}>
        <h2 style={{ margin: `0 0 ${theme.spacing.md} 0` }}>Deal Room (Placeholder)</h2>
        <p style={{ color: theme.colors.textSecondary }}>
          This is a placeholder for the Deal Room workspace. Full implementation will be added in Chunk 5.
        </p>
        <p style={{ color: theme.colors.textSecondary, marginTop: theme.spacing.md }}>
          Deal ID: {dealId}
        </p>
      </div>
    </div>
  );
}

export default DealRoom;
