import React, { useEffect, useState } from 'react';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Button from './Button';
import Badge from './Badge';
import { CheckCircle, Clock, Lock, Send, Filter } from 'lucide-react';

/**
 * Task Board component - Shows tasks organized by stage with filters
 * Chunk 6: Timeline + Task engine
 */
function TaskBoard({ dealId }) {
  const [tasks, setTasks] = useState([]);
  const [stages, setStages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeFilter, setActiveFilter] = useState('all');
  const [chasingTaskId, setChasingTaskId] = useState(null);

  useEffect(() => {
    loadTasks();
    loadStages();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [dealId, activeFilter]);

  async function loadTasks() {
    setLoading(true);
    setError(null);
    try {
      let url = `/api/deal-tasks/?deal_id=${dealId}`;
      if (activeFilter !== 'all') {
        url = `/api/deal-tasks/filters/?deal_id=${dealId}&filter=${activeFilter}`;
      }
      const res = await api.get(url);
      setTasks(res.data || []);
    } catch (err) {
      console.error('Failed to load tasks:', err);
      setError('Failed to load tasks');
    } finally {
      setLoading(false);
    }
  }

  async function loadStages() {
    try {
      // Load stages from deal
      const stagesRes = await api.get(`/api/deals/${dealId}/timeline/`);
      setStages(stagesRes.data?.stages || []);
    } catch (err) {
      console.error('Failed to load stages:', err);
    }
  }

  async function handleCompleteTask(taskId) {
    try {
      await api.post(`/api/deal-tasks/${taskId}/complete/`);
      loadTasks(); // Reload tasks
    } catch (err) {
      console.error('Failed to complete task:', err);
      alert(err.response?.data?.error || 'Failed to complete task. ' + (err.response?.data?.blocking_tasks ? `Blocked by: ${err.response.data.blocking_tasks.join(', ')}` : ''));
    }
  }

  async function handleChaseTask(taskId) {
    setChasingTaskId(taskId);
    try {
      const res = await api.post(`/api/deal-tasks/${taskId}/chase/`);
      alert(res.data.message || 'Chase reminder sent');
    } catch (err) {
      console.error('Failed to send chase:', err);
      alert(err.response?.data?.error || 'Failed to send chase reminder');
    } finally {
      setChasingTaskId(null);
    }
  }

  const getTaskStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle size={16} color={theme.colors.success} />;
      case 'in_progress':
        return <Clock size={16} color={theme.colors.primary} />;
      case 'blocked':
        return <Lock size={16} color={theme.colors.error} />;
      default:
        return <Clock size={16} color={theme.colors.textSecondary} />;
    }
  };

  const getTaskStatusBadge = (task) => {
    if (task.is_overdue) {
      return { variant: 'error', label: 'Overdue' };
    }
    const statusMap = {
      completed: { variant: 'success', label: 'Completed' },
      in_progress: { variant: 'info', label: 'In Progress' },
      blocked: { variant: 'error', label: 'Blocked' },
      pending: { variant: 'secondary', label: 'Pending' },
      cancelled: { variant: 'secondary', label: 'Cancelled' },
    };
    return statusMap[task.status] || { variant: 'secondary', label: task.status_display };
  };

  const getPriorityBadge = (priority) => {
    const priorityMap = {
      critical: { variant: 'error', label: 'Critical' },
      high: { variant: 'warning', label: 'High' },
      medium: { variant: 'info', label: 'Medium' },
      low: { variant: 'secondary', label: 'Low' },
    };
    return priorityMap[priority] || { variant: 'secondary', label: priority };
  };

  // Group tasks by stage
  const tasksByStage = {};
  tasks.forEach(task => {
    const stageId = task.stage;
    if (!tasksByStage[stageId]) {
      tasksByStage[stageId] = [];
    }
    tasksByStage[stageId].push(task);
  });

  const filters = [
    { id: 'all', label: 'All Tasks' },
    { id: 'overdue', label: 'Overdue' },
    { id: 'awaiting_review', label: 'Awaiting Review' },
    { id: 'critical_path', label: 'Critical Path' },
    { id: 'blocked', label: 'Blocked' },
  ];

  if (loading) {
    return <p style={{ color: theme.colors.textSecondary }}>Loading tasks...</p>;
  }

  if (error) {
    return (
      <div style={{ ...commonStyles.alertError, marginBottom: theme.spacing.lg }}>
        {error}
      </div>
    );
  }

  return (
    <div>
      {/* Filters */}
      <div style={{ ...commonStyles.card, marginBottom: theme.spacing.lg }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.md, flexWrap: 'wrap' }}>
          <Filter size={18} color={theme.colors.textSecondary} />
          <span style={{ fontWeight: theme.typography.fontWeight.semibold }}>Filter:</span>
          {filters.map(filter => (
            <Button
              key={filter.id}
              variant={activeFilter === filter.id ? 'primary' : 'outline'}
              size="sm"
              onClick={() => setActiveFilter(filter.id)}
            >
              {filter.label}
            </Button>
          ))}
        </div>
      </div>

      {/* Tasks by Stage */}
      {stages.length > 0 ? (
        <div style={{ display: 'flex', flexDirection: 'column', gap: theme.spacing.xl }}>
          {stages.map(stage => {
            const stageTasks = tasksByStage[stage.id] || [];
            if (activeFilter !== 'all' && stageTasks.length === 0) {
              return null; // Don't show empty stages when filtering
            }

            return (
              <div key={stage.id} style={commonStyles.card}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: theme.spacing.md }}>
                  <h3 style={{ margin: 0 }}>
                    Stage {stage.stage_number}: {stage.name}
                  </h3>
                  <Badge variant={stage.status === 'in_progress' ? 'info' : 'secondary'}>
                    {stageTasks.length} task{stageTasks.length !== 1 ? 's' : ''}
                  </Badge>
                </div>

                {stageTasks.length === 0 ? (
                  <p style={{ color: theme.colors.textSecondary, fontStyle: 'italic' }}>
                    No tasks in this stage
                  </p>
                ) : (
                  <div style={{ display: 'flex', flexDirection: 'column', gap: theme.spacing.md }}>
                    {stageTasks.map(task => (
                      <div
                        key={task.id}
                        style={{
                          padding: theme.spacing.md,
                          border: `1px solid ${theme.colors.gray200}`,
                          borderRadius: theme.borderRadius.md,
                          background: task.is_overdue ? theme.colors.errorLight : theme.colors.white,
                        }}
                      >
                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: theme.spacing.xs }}>
                          <div style={{ flex: 1 }}>
                            <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.xs, marginBottom: theme.spacing.xs }}>
                              {getTaskStatusIcon(task.status)}
                              <h4 style={{ margin: 0, flex: 1 }}>{task.title}</h4>
                              <Badge variant={getTaskStatusBadge(task).variant}>
                                {getTaskStatusBadge(task).label}
                              </Badge>
                              {task.priority && (
                                <Badge variant={getPriorityBadge(task.priority).variant}>
                                  {getPriorityBadge(task.priority).label}
                                </Badge>
                              )}
                            </div>
                            {task.description && (
                              <p style={{ margin: `0 0 ${theme.spacing.xs} 0`, color: theme.colors.textSecondary, fontSize: theme.typography.fontSize.sm }}>
                                {task.description}
                              </p>
                            )}
                            <div style={{ display: 'flex', gap: theme.spacing.md, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary, flexWrap: 'wrap' }}>
                              {task.assignee_name && (
                                <span>Assignee: {task.assignee_name}</span>
                              )}
                              {task.due_date && (
                                <span>Due: {new Date(task.due_date).toLocaleString()}</span>
                              )}
                              {task.owner_party_type && (
                                <span>Owner: {task.owner_party_type}</span>
                              )}
                            </div>
                            {task.blocked_by && task.blocked_by.length > 0 && (
                              <div style={{ marginTop: theme.spacing.xs, padding: theme.spacing.xs, background: theme.colors.warningLight, borderRadius: theme.borderRadius.sm }}>
                                <p style={{ margin: 0, fontSize: theme.typography.fontSize.sm, fontWeight: theme.typography.fontWeight.semibold }}>
                                  Blocked by:
                                </p>
                                <ul style={{ margin: 0, paddingLeft: theme.spacing.lg, fontSize: theme.typography.fontSize.sm }}>
                                  {task.blocked_by.map(blocker => (
                                    <li key={blocker.id}>{blocker.title} ({blocker.status})</li>
                                  ))}
                                </ul>
                              </div>
                            )}
                            {task.dependencies_list && task.dependencies_list.length > 0 && (
                              <div style={{ marginTop: theme.spacing.xs, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
                                <span>Depends on: </span>
                                {task.dependencies_list.map((dep, idx) => (
                                  <span key={dep.id}>
                                    {dep.title} {dep.status === 'completed' ? '✓' : '⏳'}
                                    {idx < task.dependencies_list.length - 1 && ', '}
                                  </span>
                                ))}
                              </div>
                            )}
                          </div>
                        </div>
                        <div style={{ display: 'flex', gap: theme.spacing.sm, marginTop: theme.spacing.sm }}>
                          {task.status !== 'completed' && task.status !== 'cancelled' && (
                            <>
                              <Button
                                variant="success"
                                size="sm"
                                onClick={() => handleCompleteTask(task.id)}
                                disabled={task.blocked_by && task.blocked_by.length > 0}
                              >
                                <CheckCircle size={14} style={{ marginRight: theme.spacing.xs }} />
                                Complete
                              </Button>
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => handleChaseTask(task.id)}
                                disabled={chasingTaskId === task.id}
                              >
                                <Send size={14} style={{ marginRight: theme.spacing.xs }} />
                                {chasingTaskId === task.id ? 'Sending...' : 'Chase'}
                              </Button>
                            </>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      ) : (
        <div style={commonStyles.card}>
          <p style={{ color: theme.colors.textSecondary }}>No tasks found</p>
        </div>
      )}
    </div>
  );
}

export default TaskBoard;
