import React, { useEffect, useState } from 'react';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Badge from './Badge';
import { Clock, CheckCircle, AlertCircle, PlayCircle } from 'lucide-react';

/**
 * Timeline component - Shows deal stages and events
 * Chunk 6: Timeline + Task engine
 */
function Timeline({ dealId }) {
  const [timeline, setTimeline] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadTimeline();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [dealId]);

  async function loadTimeline() {
    setLoading(true);
    setError(null);
    try {
      const res = await api.get(`/api/deals/${dealId}/timeline/`);
      setTimeline(res.data);
    } catch (err) {
      console.error('Failed to load timeline:', err);
      setError('Failed to load timeline');
    } finally {
      setLoading(false);
    }
  }

  if (loading) {
    return <p style={{ color: theme.colors.textSecondary }}>Loading timeline...</p>;
  }

  if (error) {
    return (
      <div style={{ ...commonStyles.alertError, marginBottom: theme.spacing.lg }}>
        {error}
      </div>
    );
  }

  if (!timeline) {
    return <p style={{ color: theme.colors.textSecondary }}>No timeline data available</p>;
  }

  const getStageStatusIcon = (status) => {
    switch (status) {
      case 'completed':
        return <CheckCircle size={20} color={theme.colors.success} />;
      case 'in_progress':
        return <PlayCircle size={20} color={theme.colors.primary} />;
      case 'blocked':
        return <AlertCircle size={20} color={theme.colors.error} />;
      default:
        return <Clock size={20} color={theme.colors.textSecondary} />;
    }
  };

  const getStageStatusBadge = (status) => {
    const statusMap = {
      completed: { variant: 'success', label: 'Completed' },
      in_progress: { variant: 'info', label: 'In Progress' },
      blocked: { variant: 'error', label: 'Blocked' },
      not_started: { variant: 'secondary', label: 'Not Started' },
    };
    return statusMap[status] || { variant: 'secondary', label: status };
  };

  return (
    <div style={commonStyles.card}>
      <h2 style={{ margin: `0 0 ${theme.spacing.lg} 0` }}>Deal Timeline</h2>

      {/* Stages */}
      <div style={{ marginBottom: theme.spacing.xl }}>
        <h3 style={{ ...commonStyles.subHeader, marginBottom: theme.spacing.md }}>
          Stages
        </h3>
        <div style={{ display: 'flex', flexDirection: 'column', gap: theme.spacing.md }}>
          {timeline.stages && timeline.stages.map((stage, index) => (
            <div
              key={stage.id}
              style={{
                padding: theme.spacing.md,
                border: `1px solid ${theme.colors.gray200}`,
                borderRadius: theme.borderRadius.md,
                background: stage.status === 'in_progress' ? theme.colors.primaryLight : theme.colors.white,
              }}
            >
              <div style={{ display: 'flex', alignItems: 'center', gap: theme.spacing.sm, marginBottom: theme.spacing.xs }}>
                {getStageStatusIcon(stage.status)}
                <h4 style={{ margin: 0, flex: 1 }}>
                  Stage {stage.stage_number}: {stage.name}
                </h4>
                <Badge variant={getStageStatusBadge(stage.status).variant}>
                  {getStageStatusBadge(stage.status).label}
                </Badge>
              </div>
              {stage.description && (
                <p style={{ margin: `0 0 ${theme.spacing.sm} 0`, color: theme.colors.textSecondary, fontSize: theme.typography.fontSize.sm }}>
                  {stage.description}
                </p>
              )}
              <div style={{ display: 'flex', gap: theme.spacing.lg, fontSize: theme.typography.fontSize.sm, color: theme.colors.textSecondary }}>
                {stage.entered_at && (
                  <span>Started: {new Date(stage.entered_at).toLocaleString()}</span>
                )}
                {stage.completed_at && (
                  <span>Completed: {new Date(stage.completed_at).toLocaleString()}</span>
                )}
                {stage.sla_target_days && (
                  <span>SLA: {stage.sla_target_days} days</span>
                )}
              </div>
              {stage.exit_criteria && stage.exit_criteria.length > 0 && (
                <div style={{ marginTop: theme.spacing.sm }}>
                  <p style={{ margin: `0 0 ${theme.spacing.xs} 0`, fontSize: theme.typography.fontSize.sm, fontWeight: theme.typography.fontWeight.semibold }}>
                    Exit Criteria:
                  </p>
                  <ul style={{ margin: 0, paddingLeft: theme.spacing.lg, fontSize: theme.typography.fontSize.sm }}>
                    {stage.exit_criteria.map((criteria, idx) => (
                      <li key={idx}>{criteria}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Recent Tasks */}
      {timeline.tasks && timeline.tasks.length > 0 && (
        <div style={{ marginBottom: theme.spacing.xl }}>
          <h3 style={{ ...commonStyles.subHeader, marginBottom: theme.spacing.md }}>
            Recent Tasks
          </h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: theme.spacing.sm }}>
            {timeline.tasks.slice(0, 10).map((task) => (
              <div
                key={task.id}
                style={{
                  padding: theme.spacing.sm,
                  border: `1px solid ${theme.colors.gray200}`,
                  borderRadius: theme.borderRadius.sm,
                  fontSize: theme.typography.fontSize.sm,
                }}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <span style={{ fontWeight: theme.typography.fontWeight.semibold }}>{task.title}</span>
                  <Badge variant={task.status === 'completed' ? 'success' : 'secondary'}>
                    {task.status_display}
                  </Badge>
                </div>
                {task.completed_at && (
                  <span style={{ color: theme.colors.textSecondary, fontSize: theme.typography.fontSize.xs }}>
                    Completed: {new Date(task.completed_at).toLocaleString()}
                  </span>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Audit Events */}
      {timeline.audit_events && timeline.audit_events.length > 0 && (
        <div>
          <h3 style={{ ...commonStyles.subHeader, marginBottom: theme.spacing.md }}>
            Recent Activity
          </h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: theme.spacing.sm }}>
            {timeline.audit_events.slice(0, 10).map((event) => (
              <div
                key={event.id}
                style={{
                  padding: theme.spacing.sm,
                  border: `1px solid ${theme.colors.gray200}`,
                  borderRadius: theme.borderRadius.sm,
                  fontSize: theme.typography.fontSize.sm,
                }}
              >
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                  <span>{event.event_type_display}</span>
                  <span style={{ color: theme.colors.textSecondary }}>
                    {new Date(event.timestamp).toLocaleString()}
                  </span>
                </div>
                {event.actor_user_name && (
                  <span style={{ color: theme.colors.textSecondary, fontSize: theme.typography.fontSize.xs }}>
                    by {event.actor_user_name}
                  </span>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default Timeline;
