import React, { useState, useEffect } from 'react';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Button from './Button';
import Input from './Input';
import Textarea from './Textarea';
import Select from './Select';
import Badge from './Badge';

function InformationRequestBuilder({ applicationId, onClose, onSuccess }) {
  const [title, setTitle] = useState('');
  const [notes, setNotes] = useState('');
  const [items, setItems] = useState([
    { title: '', description: '', due_date: '', document_type_id: '' }
  ]);
  const [documentTypes, setDocumentTypes] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadDocumentTypes();
  }, []);

  async function loadDocumentTypes() {
    try {
      const res = await api.get('/api/document-types/');
      setDocumentTypes(res.data || []);
    } catch (err) {
      console.error('Failed to load document types:', err);
    }
  }

  function addItem() {
    setItems([...items, { title: '', description: '', due_date: '', document_type_id: '' }]);
  }

  function removeItem(index) {
    setItems(items.filter((_, i) => i !== index));
  }

  function updateItem(index, field, value) {
    const updated = [...items];
    updated[index] = { ...updated[index], [field]: value };
    setItems(updated);
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setError(null);

    // Validate
    if (!title.trim()) {
      setError('Please provide a title for the request');
      return;
    }

    const validItems = items.filter(item => item.title.trim());
    if (validItems.length === 0) {
      setError('Please add at least one request item');
      return;
    }

    setLoading(true);
    try {
      const payload = {
        title: title.trim(),
        notes: notes.trim(),
        items: validItems.map(item => ({
          title: item.title.trim(),
          description: item.description.trim(),
          due_date: item.due_date || null,
          document_type_id: item.document_type_id || null,
        }))
      };

      await api.post(`/api/applications/${applicationId}/information-requests/`, payload);
      if (onSuccess) onSuccess();
      if (onClose) onClose();
    } catch (err) {
      console.error('Failed to create information request:', err);
      // Show detailed error message
      let errorMessage = 'Failed to create information request';
      if (err.response?.data) {
        if (err.response.data.error) {
          errorMessage = err.response.data.error;
        } else if (err.response.data.detail) {
          errorMessage = err.response.data.detail;
        } else if (typeof err.response.data === 'object') {
          // Handle validation errors
          const errors = Object.entries(err.response.data)
            .map(([key, value]) => `${key}: ${Array.isArray(value) ? value.join(', ') : value}`)
            .join('\n');
          errorMessage = `Validation errors:\n${errors}`;
        }
      } else if (err.message) {
        errorMessage = err.message;
      }
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000,
    }}>
      <div style={{
        background: theme.colors.white,
        borderRadius: theme.borderRadius.lg,
        padding: theme.spacing.xl,
        maxWidth: '800px',
        width: '90%',
        maxHeight: '90vh',
        overflowY: 'auto',
        boxShadow: theme.shadows.xl,
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: theme.spacing.lg }}>
          <h2 style={{ margin: 0 }}>Create Information Request</h2>
          <button
            onClick={onClose}
            style={{
              background: 'transparent',
              border: 'none',
              fontSize: '24px',
              cursor: 'pointer',
              color: theme.colors.textSecondary,
            }}
          >
            Ã—
          </button>
        </div>

        {error && (
          <div style={{
            padding: theme.spacing.md,
            background: theme.colors.errorLight,
            color: theme.colors.errorDark,
            borderRadius: theme.borderRadius.md,
            marginBottom: theme.spacing.lg,
          }}>
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit}>
          <div style={{ marginBottom: theme.spacing.lg }}>
            <label style={{
              display: 'block',
              marginBottom: theme.spacing.xs,
              fontWeight: theme.typography.fontWeight.medium,
            }}>
              Request Title *
            </label>
            <Input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="e.g., Additional Financial Documents"
              required
            />
          </div>

          <div style={{ marginBottom: theme.spacing.lg }}>
            <label style={{
              display: 'block',
              marginBottom: theme.spacing.xs,
              fontWeight: theme.typography.fontWeight.medium,
            }}>
              Notes (optional)
            </label>
            <Textarea
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Additional instructions or context..."
              rows={3}
            />
          </div>

          <div style={{ marginBottom: theme.spacing.lg }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: theme.spacing.md }}>
              <label style={{
                fontWeight: theme.typography.fontWeight.medium,
              }}>
                Request Items *
              </label>
              <Button type="button" variant="outline" size="sm" onClick={addItem}>
                + Add Item
              </Button>
            </div>

            {items.map((item, index) => (
              <div key={index} style={{
                padding: theme.spacing.md,
                border: `1px solid ${theme.colors.gray200}`,
                borderRadius: theme.borderRadius.md,
                marginBottom: theme.spacing.md,
                background: theme.colors.gray50,
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: theme.spacing.sm }}>
                  <strong>Item {index + 1}</strong>
                  {items.length > 1 && (
                    <Button
                      type="button"
                      variant="error"
                      size="sm"
                      onClick={() => removeItem(index)}
                    >
                      Remove
                    </Button>
                  )}
                </div>

                <div style={{ marginBottom: theme.spacing.sm }}>
                  <label style={{
                    display: 'block',
                    marginBottom: theme.spacing.xs,
                    fontSize: theme.typography.fontSize.sm,
                  }}>
                    Title *
                  </label>
                  <Input
                    value={item.title}
                    onChange={(e) => updateItem(index, 'title', e.target.value)}
                    placeholder="e.g., Bank Statements (Last 3 months)"
                    required
                  />
                </div>

                <div style={{ marginBottom: theme.spacing.sm }}>
                  <label style={{
                    display: 'block',
                    marginBottom: theme.spacing.xs,
                    fontSize: theme.typography.fontSize.sm,
                  }}>
                    Description
                  </label>
                  <Textarea
                    value={item.description}
                    onChange={(e) => updateItem(index, 'description', e.target.value)}
                    placeholder="Detailed description of what is required..."
                    rows={2}
                  />
                </div>

                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: theme.spacing.sm }}>
                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: theme.spacing.xs,
                      fontSize: theme.typography.fontSize.sm,
                    }}>
                      Due Date
                    </label>
                    <Input
                      type="date"
                      value={item.due_date}
                      onChange={(e) => updateItem(index, 'due_date', e.target.value)}
                    />
                  </div>

                  <div>
                    <label style={{
                      display: 'block',
                      marginBottom: theme.spacing.xs,
                      fontSize: theme.typography.fontSize.sm,
                    }}>
                      Document Type
                    </label>
                    <Select
                      value={item.document_type_id}
                      onChange={(e) => updateItem(index, 'document_type_id', e.target.value)}
                    >
                      <option value="">Select type...</option>
                      {documentTypes.map(dt => (
                        <option key={dt.id} value={dt.id}>{dt.name}</option>
                      ))}
                    </Select>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div style={{ display: 'flex', gap: theme.spacing.md, justifyContent: 'flex-end' }}>
            <Button type="button" variant="outline" onClick={onClose} disabled={loading}>
              Cancel
            </Button>
            <Button type="submit" variant="primary" disabled={loading}>
              {loading ? 'Creating...' : 'Create Request'}
            </Button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default InformationRequestBuilder;
