import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../api';
import { theme, commonStyles } from '../styles/theme';
import Wizard from '../components/Wizard';
import Input from '../components/Input';
import Select from '../components/Select';
import Textarea from '../components/Textarea';
import Checkbox from '../components/Checkbox';
import Button from '../components/Button';
import CompanyDetails from '../components/CompanyDetails';

function ConsultantProfileWizard() {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [message, setMessage] = useState(null);
  const [profile, setProfile] = useState(null);

  // Step 1: Account setup and consent
  const [step1Data, setStep1Data] = useState({
    email: '',
    mobile_number: '',
    mfa_enabled: false,
    consent_privacy: false,
    consent_terms: false,
    consent_credit_search: false,
  });

  // Step 2: Company verification
  const [step2Data, setStep2Data] = useState({
    company_number: '',
    company_name: '',
    company_search_type: 'number',
    company_data: null,
    company_search_results: [],
    show_search_results: false,
    trading_address: '',
    primary_contact_email: '',
    primary_contact_phone: '',
    confirmed: false,
  });

  // Step 3: Directors and shareholders (for reference, user assignment)
  const [step3Data, setStep3Data] = useState({
    directors: [],
    shareholders: [],
    key_personnel_confirmed: [],
    account_holder_is_director: false,
    account_holder_name: '',
    account_holder_role: '',
  });

  // Step 4: Key contact details
  const [step4Data, setStep4Data] = useState({
    primary_contact_email: '',
    primary_contact_phone: '',
    website: '',
    address_line_1: '',
    address_line_2: '',
    city: '',
    county: '',
    postcode: '',
    country: 'United Kingdom',
  });

  // Step 5: Services & Qualifications (renumbered from step 2)
  const [step5Data, setStep5Data] = useState({
    primary_service: '',
    services_offered: [],
    qualifications: [],
    professional_registration_numbers: {
      rics: '',
      sra: '',
      cilex: '',
    },
  });

  // Step 6: Service Details (renumbered from step 4)
  const [step6Data, setStep6Data] = useState({
    service_description: '',
    years_of_experience: '',
    team_size: '',
    key_personnel: [],
    geographic_coverage: [],
  });

  // Step 7: Capacity & Availability (renumbered from step 5)
  const [step7Data, setStep7Data] = useState({
    current_capacity: '10',
    max_capacity: '20',
    average_response_time_days: '3',
  });

  // Step 8: Insurance & Compliance (renumbered from step 6)
  const [step8Data, setStep8Data] = useState({
    insurance_details: {
      provider: '',
      policy_number: '',
      expiry_date: '',
      coverage_amount: '',
    },
    compliance_certifications: [],
  });

  const steps = [
    'Account & Consent',
    'Company Verification',
    'Company Personnel',
    'Key Contact Details',
    'Services & Qualifications',
    'Service Details',
    'Capacity & Availability',
    'Insurance & Compliance',
  ];

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await api.get('/api/consultants/profiles/');
      const data = Array.isArray(response.data) ? response.data[0] : response.data;
      if (data) {
        setProfile(data);
        // Pre-fill form data
        setStep1Data({
          organisation_name: data.organisation_name || '',
          trading_name: data.trading_name || '',
          company_registration_number: data.company_registration_number || '',
          company_data: data.company_data || null,
          confirmed: !!data.company_registration_number,
        });
        setStep2Data({
          primary_service: data.primary_service || '',
          services_offered: data.services_offered || [],
          qualifications: data.qualifications || [],
          professional_registration_numbers: data.professional_registration_numbers || {
            rics: '',
            sra: '',
            cilex: '',
          },
        });
        setStep3Data({
          contact_email: data.contact_email || '',
          contact_phone: data.contact_phone || '',
          website: data.website || '',
          address_line_1: data.address_line_1 || '',
          address_line_2: data.address_line_2 || '',
          city: data.city || '',
          county: data.county || '',
          postcode: data.postcode || '',
          country: data.country || 'United Kingdom',
          geographic_coverage: data.geographic_coverage || [],
        });
        setStep4Data({
          service_description: data.service_description || '',
          years_of_experience: data.years_of_experience?.toString() || '',
          team_size: data.team_size?.toString() || '',
          key_personnel: data.key_personnel || [],
        });
        setStep5Data({
          current_capacity: data.current_capacity?.toString() || '10',
          max_capacity: data.max_capacity?.toString() || '20',
          average_response_time_days: data.average_response_time_days?.toString() || '3',
        });
        setStep6Data({
          insurance_details: data.insurance_details || {
            provider: '',
            policy_number: '',
            expiry_date: '',
            coverage_amount: '',
          },
          compliance_certifications: data.compliance_certifications || [],
        });
      }
    } catch (err) {
      console.error('Failed to fetch profile:', err);
      if (err.response?.status !== 404) {
        setError('Failed to load profile');
      }
    }
  };

  const handleCompanySearch = async () => {
    if (!step1Data.company_registration_number) {
      setError('Please enter a company number');
      return;
    }

    setLoading(true);
    setError(null);
    try {
      const response = await api.post('/api/consultants/verify-and-import-company/', {
        company_number: step1Data.company_registration_number,
      });
      setStep1Data({
        ...step1Data,
        company_data: response.data,
        organisation_name: response.data.company_name || step1Data.organisation_name,
        confirmed: true,
      });
      setMessage('Company verified successfully');
    } catch (err) {
      console.error('Company verification failed:', err);
      setError(err.response?.data?.error || 'Failed to verify company');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveAndContinue = async () => {
    setError(null);
    setMessage(null);

    if (step < steps.length) {
      // Auto-save current step
      try {
        const payload = buildPayload();
        if (profile) {
          await api.put(`/api/consultants/profiles/${profile.id}/`, payload);
        } else {
          const response = await api.post('/api/consultants/profiles/', payload);
          setProfile(response.data);
        }
        setStep(step + 1);
      } catch (err) {
        console.error('Save failed:', err);
        setError(err.response?.data?.detail || 'Failed to save profile');
      }
    } else {
      // Final step - complete profile
      await handleSubmit();
    }
  };

  const handleSubmit = async () => {
    setLoading(true);
    setError(null);
    setMessage(null);

    try {
      const payload = buildPayload();
      if (profile) {
        await api.put(`/api/consultants/profiles/${profile.id}/`, payload);
        setMessage('Profile updated successfully');
      } else {
        await api.post('/api/consultants/profiles/', payload);
        setMessage('Profile created successfully');
      }
      setTimeout(() => {
        navigate('/consultant/profile');
      }, 1500);
    } catch (err) {
      console.error('Submit failed:', err);
      setError(err.response?.data?.detail || 'Failed to save profile');
    } finally {
      setLoading(false);
    }
  };

  const buildPayload = () => {
    // Clean up professional registration numbers - only include non-empty values
    const regNumbers = {};
    if (step2Data.professional_registration_numbers.rics) {
      regNumbers.rics = step2Data.professional_registration_numbers.rics;
    }
    if (step2Data.professional_registration_numbers.sra) {
      regNumbers.sra = step2Data.professional_registration_numbers.sra;
    }
    if (step2Data.professional_registration_numbers.cilex) {
      regNumbers.cilex = step2Data.professional_registration_numbers.cilex;
    }

    return {
      organisation_name: step1Data.organisation_name,
      trading_name: step1Data.trading_name,
      company_registration_number: step1Data.company_registration_number,
      primary_service: step2Data.primary_service,
      services_offered: step2Data.services_offered,
      qualifications: step2Data.qualifications,
      professional_registration_numbers: regNumbers,
      contact_email: step3Data.contact_email,
      contact_phone: step3Data.contact_phone,
      website: step3Data.website,
      address_line_1: step3Data.address_line_1,
      address_line_2: step3Data.address_line_2,
      city: step3Data.city,
      county: step3Data.county,
      postcode: step3Data.postcode,
      country: step3Data.country,
      geographic_coverage: step3Data.geographic_coverage,
      service_description: step4Data.service_description,
      years_of_experience: step4Data.years_of_experience ? parseInt(step4Data.years_of_experience) : null,
      team_size: step4Data.team_size ? parseInt(step4Data.team_size) : null,
      key_personnel: step4Data.key_personnel,
      current_capacity: parseInt(step5Data.current_capacity),
      max_capacity: parseInt(step5Data.max_capacity),
      average_response_time_days: parseInt(step5Data.average_response_time_days),
      insurance_details: step6Data.insurance_details,
      compliance_certifications: step6Data.compliance_certifications,
    };
  };

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <div>
            <h2 style={{ marginBottom: theme.spacing.lg }}>Organisation & Company</h2>
            <div style={{ display: 'grid', gap: theme.spacing.md }}>
              <Input
                label="Organisation Name *"
                name="organisation_name"
                value={step1Data.organisation_name}
                onChange={(e) => setStep1Data({ ...step1Data, organisation_name: e.target.value })}
                required
              />
              <Input
                label="Trading Name (if different)"
                name="trading_name"
                value={step1Data.trading_name}
                onChange={(e) => setStep1Data({ ...step1Data, trading_name: e.target.value })}
              />
              <div>
                <Input
                  label="Company Registration Number"
                  name="company_registration_number"
                  value={step1Data.company_registration_number}
                  onChange={(e) => setStep1Data({ ...step1Data, company_registration_number: e.target.value })}
                  placeholder="e.g., 12345678"
                />
                <Button
                  onClick={handleCompanySearch}
                  loading={loading}
                  disabled={!step1Data.company_registration_number}
                  style={{ marginTop: theme.spacing.sm }}
                >
                  Verify Company
                </Button>
              </div>
              {step1Data.company_data && (
                <CompanyDetails
                  companyNumber={step1Data.company_registration_number}
                  apiBasePath="/api/verification/company"
                />
              )}
            </div>
          </div>
        );

      case 2:
        return (
          <div>
            <h2 style={{ marginBottom: theme.spacing.lg }}>Services & Qualifications</h2>
            <div style={{ display: 'grid', gap: theme.spacing.md }}>
              <Select
                label="Primary Service Type *"
                name="primary_service"
                value={step2Data.primary_service}
                onChange={(e) => setStep2Data({ ...step2Data, primary_service: e.target.value })}
                options={[
                  { value: '', label: 'Select service type' },
                  { value: 'solicitor', label: 'Solicitor' },
                  { value: 'monitoring_surveyor', label: 'Monitoring Surveyor' },
                  { value: 'valuation', label: 'Valuation' },
                  { value: 'other', label: 'Other Professional Service' },
                ]}
                required
              />
              <div>
                <label style={{ ...commonStyles.label, marginBottom: theme.spacing.sm, display: 'block' }}>
                  Services Offered *
                </label>
                {['solicitor', 'monitoring_surveyor', 'valuation', 'other'].map((service) => (
                  <Checkbox
                    key={service}
                    label={service.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                    checked={step2Data.services_offered.includes(service)}
                    onChange={(e) => {
                      const services = e.target.checked
                        ? [...step2Data.services_offered, service]
                        : step2Data.services_offered.filter(s => s !== service);
                      setStep2Data({ ...step2Data, services_offered: services });
                    }}
                  />
                ))}
              </div>
              <div>
                <label style={{ ...commonStyles.label, marginBottom: theme.spacing.sm, display: 'block' }}>
                  Qualifications
                </label>
                {['rics', 'rics_monitoring', 'rics_valuation', 'sra', 'cilex', 'other'].map((qual) => (
                  <Checkbox
                    key={qual}
                    label={qual.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                    checked={step2Data.qualifications.includes(qual)}
                    onChange={(e) => {
                      const quals = e.target.checked
                        ? [...step2Data.qualifications, qual]
                        : step2Data.qualifications.filter(q => q !== qual);
                      setStep2Data({ ...step2Data, qualifications: quals });
                    }}
                  />
                ))}
              </div>
              <Input
                label="RICS Number"
                name="rics_number"
                value={step2Data.professional_registration_numbers.rics}
                onChange={(e) => setStep2Data({
                  ...step2Data,
                  professional_registration_numbers: {
                    ...step2Data.professional_registration_numbers,
                    rics: e.target.value,
                  },
                })}
              />
              <Input
                label="SRA Number"
                name="sra_number"
                value={step2Data.professional_registration_numbers.sra}
                onChange={(e) => setStep2Data({
                  ...step2Data,
                  professional_registration_numbers: {
                    ...step2Data.professional_registration_numbers,
                    sra: e.target.value,
                  },
                })}
              />
              <Input
                label="CILEX Number"
                name="cilex_number"
                value={step2Data.professional_registration_numbers.cilex}
                onChange={(e) => setStep2Data({
                  ...step2Data,
                  professional_registration_numbers: {
                    ...step2Data.professional_registration_numbers,
                    cilex: e.target.value,
                  },
                })}
              />
            </div>
          </div>
        );

      case 3:
        return (
          <div>
            <h2 style={{ marginBottom: theme.spacing.lg }}>Contact & Location</h2>
            <div style={{ display: 'grid', gap: theme.spacing.md }}>
              <Input
                label="Contact Email *"
                name="contact_email"
                type="email"
                value={step3Data.contact_email}
                onChange={(e) => setStep3Data({ ...step3Data, contact_email: e.target.value })}
                required
              />
              <Input
                label="Contact Phone"
                name="contact_phone"
                value={step3Data.contact_phone}
                onChange={(e) => setStep3Data({ ...step3Data, contact_phone: e.target.value })}
              />
              <Input
                label="Website"
                name="website"
                type="url"
                value={step3Data.website}
                onChange={(e) => setStep3Data({ ...step3Data, website: e.target.value })}
              />
              <Input
                label="Address Line 1"
                name="address_line_1"
                value={step3Data.address_line_1}
                onChange={(e) => setStep3Data({ ...step3Data, address_line_1: e.target.value })}
              />
              <Input
                label="Address Line 2"
                name="address_line_2"
                value={step3Data.address_line_2}
                onChange={(e) => setStep3Data({ ...step3Data, address_line_2: e.target.value })}
              />
              <Input
                label="City"
                name="city"
                value={step3Data.city}
                onChange={(e) => setStep3Data({ ...step3Data, city: e.target.value })}
              />
              <Input
                label="County"
                name="county"
                value={step3Data.county}
                onChange={(e) => setStep3Data({ ...step3Data, county: e.target.value })}
              />
              <Input
                label="Postcode"
                name="postcode"
                value={step3Data.postcode}
                onChange={(e) => setStep3Data({ ...step3Data, postcode: e.target.value })}
              />
              <Input
                label="Country"
                name="country"
                value={step3Data.country}
                onChange={(e) => setStep3Data({ ...step3Data, country: e.target.value })}
              />
              <Textarea
                label="Geographic Coverage (one per line)"
                name="geographic_coverage"
                value={step3Data.geographic_coverage.join('\n')}
                onChange={(e) => setStep3Data({
                  ...step3Data,
                  geographic_coverage: e.target.value.split('\n').filter(line => line.trim()),
                })}
                rows={4}
              />
            </div>
          </div>
        );

      case 4:
        return (
          <div>
            <h2 style={{ marginBottom: theme.spacing.lg }}>Service Details</h2>
            <div style={{ display: 'grid', gap: theme.spacing.md }}>
              <Textarea
                label="Service Description"
                name="service_description"
                value={step4Data.service_description}
                onChange={(e) => setStep4Data({ ...step4Data, service_description: e.target.value })}
                rows={6}
              />
              <Input
                label="Years of Experience"
                name="years_of_experience"
                type="number"
                value={step4Data.years_of_experience}
                onChange={(e) => setStep4Data({ ...step4Data, years_of_experience: e.target.value })}
              />
              <Input
                label="Team Size"
                name="team_size"
                type="number"
                value={step4Data.team_size}
                onChange={(e) => setStep4Data({ ...step4Data, team_size: e.target.value })}
              />
              <Textarea
                label="Key Personnel (JSON format)"
                name="key_personnel"
                value={JSON.stringify(step4Data.key_personnel, null, 2)}
                onChange={(e) => {
                  try {
                    const parsed = JSON.parse(e.target.value);
                    setStep4Data({ ...step4Data, key_personnel: Array.isArray(parsed) ? parsed : [] });
                  } catch (err) {
                    // Invalid JSON, keep as is
                  }
                }}
                rows={6}
                placeholder='[{"name": "John Doe", "role": "Senior Partner", "qualifications": "RICS"}]'
              />
            </div>
          </div>
        );

      case 5:
        return (
          <div>
            <h2 style={{ marginBottom: theme.spacing.lg }}>Capacity & Availability</h2>
            <div style={{ display: 'grid', gap: theme.spacing.md }}>
              <Input
                label="Current Capacity (active projects)"
                name="current_capacity"
                type="number"
                value={step5Data.current_capacity}
                onChange={(e) => setStep5Data({ ...step5Data, current_capacity: e.target.value })}
              />
              <Input
                label="Maximum Capacity"
                name="max_capacity"
                type="number"
                value={step5Data.max_capacity}
                onChange={(e) => setStep5Data({ ...step5Data, max_capacity: e.target.value })}
              />
              <Input
                label="Average Response Time (days)"
                name="average_response_time_days"
                type="number"
                value={step5Data.average_response_time_days}
                onChange={(e) => setStep5Data({ ...step5Data, average_response_time_days: e.target.value })}
              />
            </div>
          </div>
        );

      case 6:
        return (
          <div>
            <h2 style={{ marginBottom: theme.spacing.lg }}>Insurance & Compliance</h2>
            <div style={{ display: 'grid', gap: theme.spacing.md }}>
              <h3 style={{ fontSize: theme.typography.fontSize.lg, marginBottom: theme.spacing.md }}>
                Professional Indemnity Insurance
              </h3>
              <Input
                label="Insurance Provider"
                name="insurance_provider"
                value={step6Data.insurance_details.provider}
                onChange={(e) => setStep6Data({
                  ...step6Data,
                  insurance_details: { ...step6Data.insurance_details, provider: e.target.value },
                })}
              />
              <Input
                label="Policy Number"
                name="policy_number"
                value={step6Data.insurance_details.policy_number}
                onChange={(e) => setStep6Data({
                  ...step6Data,
                  insurance_details: { ...step6Data.insurance_details, policy_number: e.target.value },
                })}
              />
              <Input
                label="Expiry Date"
                name="expiry_date"
                type="date"
                value={step6Data.insurance_details.expiry_date}
                onChange={(e) => setStep6Data({
                  ...step6Data,
                  insurance_details: { ...step6Data.insurance_details, expiry_date: e.target.value },
                })}
              />
              <Input
                label="Coverage Amount"
                name="coverage_amount"
                value={step6Data.insurance_details.coverage_amount}
                onChange={(e) => setStep6Data({
                  ...step6Data,
                  insurance_details: { ...step6Data.insurance_details, coverage_amount: e.target.value },
                })}
              />
              <Textarea
                label="Compliance Certifications (one per line)"
                name="compliance_certifications"
                value={step6Data.compliance_certifications.join('\n')}
                onChange={(e) => setStep6Data({
                  ...step6Data,
                  compliance_certifications: e.target.value.split('\n').filter(line => line.trim()),
                })}
                rows={4}
                placeholder="e.g., ISO 9001, FCA Authorised"
              />
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div style={{ ...commonStyles.container, padding: theme.spacing.xl }}>
      {error && (
        <div style={{
          background: theme.colors.errorLight,
          color: theme.colors.errorDark,
          padding: theme.spacing.md,
          borderRadius: theme.borderRadius.md,
          marginBottom: theme.spacing.lg,
        }}>
          {error}
        </div>
      )}
      {message && (
        <div style={{
          background: theme.colors.successLight,
          color: theme.colors.successDark,
          padding: theme.spacing.md,
          borderRadius: theme.borderRadius.md,
          marginBottom: theme.spacing.lg,
        }}>
          {message}
        </div>
      )}
      <Wizard
        steps={steps}
        currentStep={step}
        onNext={handleSaveAndContinue}
        onPrevious={() => setStep(step - 1)}
        onFinish={handleSubmit}
        loading={loading}
        canGoNext={step === 1 ? step1Data.organisation_name : true}
      >
        {renderStep()}
      </Wizard>
    </div>
  );
}

export default ConsultantProfileWizard;
